example.code:
//
// Lambda style
//

// listening
notification = GCController.Notifications.ObserveDidConnect ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, MonoTouch.Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = GCController.Notifications.ObserveDidConnect (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

// Listen to all notifications posted for any object
var token = GCController.Notifications.ObserveDidConnect ((notification) => {
	Console.WriteLine ("Observed DidConnectNotification!");
};

// Listen to all notifications posted for a single object
var token = GCController.Notifications.ObserveDidConnect (objectToObserve, (notification) => {
	Console.WriteLine ($"Observed DidConnectNotification for {nameof (objectToObserve)}!");
};

// Stop listening for notifications
token.Dispose ();

//
// Lambda style
//

// listening
notification = GCController.Notifications.ObserveDidDisconnect ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
