example.code:
//
// Lambda style
//

// listening
notification = MPMediaLibrary.Notifications.ObserveDidChange ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = MPMediaLibrary.Notifications.ObserveDidChange (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

// Listen to all notifications posted for any object
var token = MPMediaLibrary.Notifications.ObserveDidChange ((notification) => {
	Console.WriteLine ("Observed DidChangeNotification!");
};

// Listen to all notifications posted for a single object
var token = MPMediaLibrary.Notifications.ObserveDidChange (objectToObserve, (notification) => {
	Console.WriteLine ($"Observed DidChangeNotification for {nameof (objectToObserve)}!");
};

// Stop listening for notifications
token.Dispose ();
