example.code:
//
// Lambda style
//

// listening
notification = MPMusicPlayerController.Notifications.ObserveNowPlayingItemDidChange ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = MPMusicPlayerController.Notifications.ObserveNowPlayingItemDidChange (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

// Listen to all notifications posted for any object
var token = MPMusicPlayerController.Notifications.ObserveNowPlayingItemDidChange ((notification) => {
	Console.WriteLine ("Observed NowPlayingItemDidChangeNotification!");
};

// Listen to all notifications posted for a single object
var token = MPMusicPlayerController.Notifications.ObserveNowPlayingItemDidChange (objectToObserve, (notification) => {
	Console.WriteLine ($"Observed NowPlayingItemDidChangeNotification for {nameof (objectToObserve)}!");
};

// Stop listening for notifications
token.Dispose ();

