example.code:
//
// The NSObjectFlag merely allocates the object and registers the
// C# class with the Objective-C runtime if necessary, but no actual
// initXxx method is invoked, that is done later in the constructor
//
// This is taken from Xamarin.iOS's source code:
//
[Export ("initWithFrame:")]
public UIView (System.Drawing.RectangleF frame) : base (NSObjectFlag.Empty)
{
// Invoke the init method now.
	var initWithFrame = new Selector ("initWithFrame:").Handle;
	if (IsDirectBinding)
		Handle = ObjCRuntime.Messaging.IntPtr_objc_msgSend_CGRect (this.Handle, initWithFrame, frame);
	else
		Handle = ObjCRuntime.Messaging.IntPtr_objc_msgSendSuper_CGRect (this.SuperHandle, initWithFrame, frame);
}

// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        MPVolumeView.WirelessRouteActiveDidChangeNotification, (notification) => {Console.WriteLine ("Received the notification MPVolumeView", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification MPVolumeView", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (MPVolumeView.WirelessRouteActiveDidChangeNotification, Callback);
}

// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        MPVolumeView.WirelessRoutesAvailableDidChangeNotification, (notification) => {Console.WriteLine ("Received the notification MPVolumeView", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification MPVolumeView", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (MPVolumeView.WirelessRoutesAvailableDidChangeNotification, Callback);
}
