example.code:
//
// Lambda style
//

// listening
notification = AVPlayerItem.Notifications.ObserveDidPlayToEndTime ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, MonoTouch.Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = AVPlayerItem.Notifications.ObserveDidPlayToEndTime (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

//
// Lambda style
//

// listening
notification = AVPlayerItem.Notifications.ObserveItemFailedToPlayToEndTime ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);

    Console.WriteLine ("Error", args.Error);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, MonoTouch.AVFoundation.AVPlayerItemErrorEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);

    Console.WriteLine ("Error", args.Error);
}

void Setup ()
{
    notification = AVPlayerItem.Notifications.ObserveItemFailedToPlayToEndTime (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

