void DrawLayerMaybeTransparent (CGContext context, RectangleF rect, bool useTransparentLayer)
{
  //Create a shadow to be applied, either to the transparency layer as a whole, or individually
	var shadowOffset = new SizeF (10, -20);
	context.SetShadow (shadowOffset, 0);
	if (useTransparentLayer) {
		context.BeginTransparencyLayer ();
	}
	context.SetRGBFillColor (0, 1, 0, 1);
	context.FillRect (new RectangleF(rect.Width / 3 + 50, rect.Top + rect.Height / 2, rect.Width / 4, rect.Height / 4));
	context.SetRGBFillColor (0, 0, 1, 1);
	context.FillRect (new RectangleF(rect.Width / 3 - 50, rect.Top + rect.Height / 2 - 100, rect.Width / 4, rect.Height / 4));
	//Make a little translucent, so shadow behavior is clearer
  context.SetRGBFillColor (1, 0, 0, 0.8f);
	context.FillRect (new RectangleF(rect.Width / 3, rect.Top + rect.Height / 2 - 50, rect.Width / 4, rect.Height / 4));
	if (useTransparentLayer) {
		context.EndTransparencyLayer ();
	}
}

public override void Draw (RectangleF rect)
{
	base.Draw (rect);

	var topHalf = new RectangleF (0, this.Bounds.Height / 2, this.Bounds.Width, this.Bounds.Height / 2);
	var bottomHalf = new RectangleF (0, 0, this.Bounds.Width, this.Bounds.Height / 2);
	using (var ctxt = UIGraphics.GetCurrentContext ()) {
		ctxt.ScaleCTM (1, -1);
		ctxt.TranslateCTM (0, -Bounds.Height);
		DrawLayerMaybeTransparent (ctxt, topHalf, true);
		DrawLayerMaybeTransparent (ctxt, bottomHalf, false);
	}
}

doc = CGPDFDocument.FromFile (Path.Combine (NSBundle.MainBundle.BundlePath, "Images/QuartzImageDrawing.pdf"));
if (doc == null)
		throw new Exception ("Could not load document");

using (var ctxt = UIGraphics.GetCurrentContext ()) {
  // PDF page drawing expects a Lower-Left coordinate system, so we flip the coordinate system
	// before we start drawing.
	context.TranslateCTM (0, Bounds.Height);
	context.ScaleCTM (1, -1);

	// Grab the first PDF page
	using (CGPDFPage page = doc.GetPage (1)){
		// We're about to modify the context CTM to draw the PDF page where we want it, so save the graphics state in case we want to do more drawing
		context.SaveState ();

		// CGPDFPageGetDrawingTransform provides an easy way to get the transform for a PDF page. It will scale down to fit, including any
		// base rotations necessary to display the PDF page correctly.

		CGAffineTransform pdfTransform = page.GetDrawingTransform (CGPDFBox.Crop, Bounds, 0, true);

		// And apply the transform.
		context.ConcatCTM (pdfTransform);
		// Finally, we draw the page and restore the graphics state for further manipulations!
		context.DrawPDFPage (page);
		context.RestoreState();
	}				
}

public override void Draw (RectangleF rect)
{
	base.Draw (rect);

	using (var ctxt = UIGraphics.GetCurrentContext ()) {
		DrawRay (ctxt, new PointF (5, 5), new PointF (105, 5), UIColor.Red.CGColor);
		DrawRay (ctxt, new PointF (5, 5), new PointF (5, 105), UIColor.Red.CGColor);
		ctxt.ScaleCTM (1, -1);
		ctxt.TranslateCTM (0, -Bounds.Height);
		DrawRay (ctxt, new PointF (5, 5), new PointF (105, 5), UIColor.Green.CGColor);
		DrawRay (ctxt, new PointF (5, 5), new PointF (5, 105), UIColor.Green.CGColor);

		var cos = .707f;
		var sin = -.707f;
		var xform = new CGAffineTransform (cos, sin, -sin, cos, Bounds.Width / 2, 100);

		ctxt.ConcatCTM (xform);
		DrawRay (ctxt, new PointF (0, 0), new PointF (100, 0), UIColor.Blue.CGColor);
		DrawRay (ctxt, new PointF (0, 0), new PointF (0, 100), UIColor.Blue.CGColor);

	}
}
