example.code:
//
// Lambda style
//

// listening
notification = NSLocale.Notifications.ObserveCurrentLocaleDidChange ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, MonoTouch.Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = NSLocale.Notifications.ObserveCurrentLocaleDidChange (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}
