example.code:
// Listen to all notifications posted for any object
var token = NSValueTransformer.Notifications.ObserveCompletedInitialSync ((notification) => {
	Console.WriteLine ("Observed CompletedInitialSyncNotification!");
};

// Listen to all notifications posted for a single object
var token = NSValueTransformer.Notifications.ObserveCompletedInitialSync (objectToObserve, (notification) => {
	Console.WriteLine ($"Observed CompletedInitialSyncNotification for {nameof (objectToObserve)}!");
};

// Stop listening for notifications
token.Dispose ();

// Listen to all notifications posted for any object
