var target = new CLLocationCoordinate2D(37.7952, -122.4028);
var viewPoint = new CLLocationCoordinate2D(37.8009, -122.4100);
//Enable 3D buildings
mapView.ShowsBuildings = true;
mapView.PitchEnabled = true;

var camera = MKMapCamera.CameraLookingAtCenterCoordinate(target, viewPoint, 500);
mapView.Camera = camera;

//
// The NSObjectFlag merely allocates the object and registers the
// C# class with the Objective-C runtime if necessary, but no actual
// initXxx method is invoked, that is done later in the constructor
//
// This is taken from MonoTouch's source code:
//
[Export ("initWithFrame:")]
public UIView (System.Drawing.RectangleF frame) : base (NSObjectFlag.Empty)
{
// Invoke the init method now.
	var initWithFrame = new Selector ("initWithFrame:").Handle;
	if (IsDirectBinding)
		Handle = MonoTouch.ObjCRuntime.Messaging.IntPtr_objc_msgSend_RectangleF (this.Handle, initWithFrame, frame);
	else
		Handle = MonoTouch.ObjCRuntime.Messaging.IntPtr_objc_msgSendSuper_RectangleF (this.SuperHandle, initWithFrame, frame);
}

var target = new CLLocationCoordinate2D(37.7952, -122.4028);
var viewPoint = new CLLocationCoordinate2D(37.8009, -122.4100);
//Enable 3D buildings
mapView.ShowsBuildings = true;
mapView.PitchEnabled = true;

var camera = MKMapCamera.CameraLookingAtCenterCoordinate(target, viewPoint, 500);
mapView.Camera = camera;

var olRenderers = new Dictionary<MKOverlay, MKOverlayRenderer>();
mapView.OverlayRenderer = (mv, ol) => olRenderers[ol];
//...etc...
var directions = new MKDirections(req);
directions.CalculateDirections((response, error) => { 
	if(error == null)
	{
		var route = response.Routes[0];
		var rteLine = new MKPolylineRenderer(route.Polyline) {
			LineWidth = 5.0f,
			StrokeColor = UIColor.Purple
		};
    //Add renderer to dictionary used by mapView.OverlayRenderer delegate
		olRenderers[route] = rteLine;
		mapView.AddOverlay(route.Polyline, MKOverlayLevel.AboveRoads);
	}
	else
	{
		Console.WriteLine(error);
	}
});
