example.code:
// Listen to all notifications posted for any object
var token = MAAudibleMedia.Notifications.ObserveSettingsChanged ((notification) => {
	Console.WriteLine ("Observed SettingsChangedNotification!");
};

// Listen to all notifications posted for a single object
var token = MAAudibleMedia.Notifications.ObserveSettingsChanged (objectToObserve, (notification) => {
	Console.WriteLine ($"Observed SettingsChangedNotification for {nameof (objectToObserve)}!");
};

// Stop listening for notifications
token.Dispose ();
