example.code:
// Listen to all notifications posted for any object
var token = UIFocusUpdateContext.Notifications.ObserveDidUpdate ((notification) => {
	Console.WriteLine ("Observed DidUpdateNotification!");
};

// Listen to all notifications posted for a single object
var token = UIFocusUpdateContext.Notifications.ObserveDidUpdate (objectToObserve, (notification) => {
	Console.WriteLine ($"Observed DidUpdateNotification for {nameof (objectToObserve)}!");
};

// Stop listening for notifications
token.Dispose ();

