example.code:
//
// Lambda style
//

// listening
notification = UIMenuController.Notifications.ObserveDidHideMenu ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, MonoTouch.Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = UIMenuController.Notifications.ObserveDidHideMenu (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

