example.code:
//
// Lambda style
//

// listening
notification = UIScreen.Notifications.ObserveBrightnessDidChange ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, MonoTouch.Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = UIScreen.Notifications.ObserveBrightnessDidChange (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

// Listen to all notifications posted for any object
var token = UIScreen.Notifications.ObserveBrightnessDidChange ((notification) => {
	Console.WriteLine ("Observed BrightnessDidChangeNotification!");
};

// Listen to all notifications posted for a single object
var token = UIScreen.Notifications.ObserveBrightnessDidChange (objectToObserve, (notification) => {
	Console.WriteLine ($"Observed BrightnessDidChangeNotification for {nameof (objectToObserve)}!");
};

// Stop listening for notifications
token.Dispose ();

// Listen to all notifications posted for any object
var token = UIScreen.Notifications.ObserveCapturedDidChange ((notification) => {
	Console.WriteLine ("Observed CapturedDidChangeNotification!");
};

// Listen to all notifications posted for a single object
var token = UIScreen.Notifications.ObserveCapturedDidChange (objectToObserve, (notification) => {
	Console.WriteLine ($"Observed CapturedDidChangeNotification for {nameof (objectToObserve)}!");
};

// Stop listening for notifications
token.Dispose ();

// Listen to all notifications posted for any object
