example.code:
//
// Lambda style
//

// listening
notification = UIWindow.Notifications.ObserveDidBecomeHidden ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, MonoTouch.Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = UIWindow.Notifications.ObserveDidBecomeHidden (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

