let fieldBehavior = UIFieldBehavior.CreateVortexField() fieldBehavior.Position <- new CGPoint(this.View.Frame.Left + this.View.Frame.Width / nfloat 2.0, this.View.Frame.Top + this.View.Frame.Height / nfloat 2.0) fieldBehavior.MinimumRadius <- nfloat 5.0
let animator = new UIDynamicAnimator(this.View)
let wanderers = seq { 20.0 .. 60.0 .. (Math.Floor(float frame.Height)) } |> Seq.map (fun y -> new CGRect(20.0, y, 20.0, 20.0)) |> Seq.map (fun frame -> let v = new UIView(frame) v.BackgroundColor <- UIColor.Red v ) |> Array.ofSeq this.View.AddSubviews(wanderers)
wanderers |> Seq.map (fun w -> new UIDynamicItemBehavior([| w :> IUIDynamicItem|])) |> Seq.iter (fun itemBehavior -> itemBehavior.Density <- nfloat 0.01 itemBehavior.Resistance <- nfloat 0.0 itemBehavior.Friction <- nfloat 0.0 itemBehavior.AllowsRotation <- true itemBehavior.Charge <- nfloat -1.0 animator.AddBehavior(itemBehavior) )
wanderers |> Seq.iter fieldBehavior.AddItem
animator.AddBehavior(fieldBehavior)

//
// The NSObjectFlag merely allocates the object and registers the
// C# class with the Objective-C runtime if necessary, but no actual
// initXxx method is invoked, that is done later in the constructor
//
// This is taken from MonoTouch's source code:
//
[Export ("initWithFrame:")]
public UIView (System.Drawing.RectangleF frame) : base (NSObjectFlag.Empty)
{
// Invoke the init method now.
	var initWithFrame = new Selector ("initWithFrame:").Handle;
	if (IsDirectBinding)
		Handle = MonoTouch.ObjCRuntime.Messaging.IntPtr_objc_msgSend_RectangleF (this.Handle, initWithFrame, frame);
	else
		Handle = MonoTouch.ObjCRuntime.Messaging.IntPtr_objc_msgSendSuper_RectangleF (this.SuperHandle, initWithFrame, frame);
}
