example.code:
//
// The NSObjectFlag merely allocates the object and registers the
// C# class with the Objective-C runtime if necessary, but no actual
// initXxx method is invoked, that is done later in the constructor
//
// This is taken from Xamarin.iOS's source code:
//
[Export ("initWithFrame:")]
public UIView (System.Drawing.RectangleF frame) : base (NSObjectFlag.Empty)
{
// Invoke the init method now.
	var initWithFrame = new Selector ("initWithFrame:").Handle;
	if (IsDirectBinding)
		Handle = ObjCRuntime.Messaging.IntPtr_objc_msgSend_CGRect (this.Handle, initWithFrame, frame);
	else
		Handle = ObjCRuntime.Messaging.IntPtr_objc_msgSendSuper_CGRect (this.SuperHandle, initWithFrame, frame);
}

//
// Lambda style
//

// listening
notification = UITextInputMode.Notifications.ObserveCurrentInputModeDidChange ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
// Method style
//
NSObject notification;
void Callback (object sender, Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = UITextInputMode.Notifications.ObserveCurrentInputModeDidChange (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        UITextInputMode.CurrentInputModeDidChangeNotification, (notification) => {Console.WriteLine ("Received the notification UITextInputMode", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification UITextInputMode", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (UITextInputMode.CurrentInputModeDidChangeNotification, Callback);
}

public void override EncodeTo (NSCoder coder){
    coder.Encode (1, key: "version");
    coder.Encode (userName, key: "userName");
    coder.Encode (hostName, key: "hostName");
